import React, { useState } from 'react';
import { Link, Outlet, useLocation } from 'react-router-dom';
import {
  FaKey,
  FaUsers,
  FaClock,
  FaPaperPlane,
  FaCog,
  FaSignOutAlt,
  FaBars,
  FaTimes,
} from 'react-icons/fa';
import icon from '../assets/icon.png';

export default function Menu() {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(true);

  const toggleSidebar = () => setIsOpen(!isOpen);

  const linkClass = (path) =>
    `flex items-center gap-3 px-4 py-2 rounded-md transition ${
      location.pathname === path
        ? 'bg-cyan-700 border border-cyan-300 text-white'
        : 'text-gray-300 hover:bg-cyan-800 hover:text-white'
    }`;

  return (
    <div className="flex min-h-screen">
      {/* Botón de apertura (solo visible en móvil o sidebar cerrado) */}
      {!isOpen && (
        <button
          onClick={toggleSidebar}
          className="fixed top-4 left-4 z-50 bg-gray-800 text-white p-2 rounded-md shadow-md md:hidden"
        >
          <FaBars />
        </button>
      )}

      {/* Sidebar */}
      <aside
        className={`bg-gray-900 text-white p-4 shadow-md flex flex-col items-center transition-all duration-300 ease-in-out z-40 ${
          isOpen ? 'w-64' : 'w-0 overflow-hidden'
        } fixed md:static h-full`}
      >
        {/* Botón cerrar (solo en móvil) */}
        <div className="w-full flex justify-end md:hidden mb-2">
          <button onClick={toggleSidebar} className="text-white">
            <FaTimes />
          </button>
        </div>

        {/* Logo */}
        <div className="mb-6 text-center">
          <img
            src={icon}
            alt="KeyFort Logo"
            className="w-16 h-16 mx-auto mb-2"
          />
          <h1 className="text-2xl font-bold">KEYFORT</h1>
        </div>

        {/* Links */}
        <nav className="flex flex-col w-full space-y-2 text-md font-medium">
          <Link to="/Passwords" className={linkClass('/Passwords')}>
            <FaKey />
            <span>Contraseñas</span>
          </Link>
          <Link to="/Groups" className={linkClass('/Groups')}>
            <FaUsers />
            <span>Grupos</span>
          </Link>
          <Link to="/AccessHistory" className={linkClass('/AccessHistory')}>
            <FaClock />
            <span>Historial de acceso</span>
          </Link>
          <Link to="/Send" className={linkClass('/Send')}>
            <FaPaperPlane />
            <span>Enviar Contraseña</span>
          </Link>
          <Link to="/Settings" className={linkClass('/Settings')}>
            <FaCog />
            <span>Ajustes</span>
          </Link>
          <Link to="/logout" className={linkClass('/logout')}>
            <FaSignOutAlt />
            <span>Cerrar Sesión</span>
          </Link>
        </nav>
      </aside>

      {/* Contenido */}
      <main className="flex-1 bg-gray-100 p-6 md:ml-64">
        <Outlet />
      </main>
    </div>
  );
}
