import { React, useState } from 'react'
import { FaPlus, FaEllipsisV } from 'react-icons/fa'

export default function AddUser() {
	const [showModal, setShowModal] = useState(false);
	const [selectedUser, setSelectedUser] = useState(null);
	const [contextMenuUser, setContextMenuUser] = useState(null);

	return (
		<div className='text-3xl p-10 font-mono w-full text-md font-medium transition-all relative'>
			<p className='p-10 text-6xl text-white'>Usuarios</p>
			<nav className='justify-items-end px-5'>
				<button
					onClick={() => {
						setSelectedUser(null);
						setShowModal(true);
					}}
					className="flex flex-row items-center gap-3 px-4 py-2 rounded-md text-white text-xl bg-cyan-700 border border-cyan-300 hover:bg-cyan-800"
				>
					<FaPlus />
					<span>Add User</span>
				</button>
			</nav>

			<section className='p-5 space-y-5'>
				{/* Primer usuario con eventos */}
				<article className='relative flex flex-row justify-between items-center bg-gray-900 text-white p-4 rounded-xl border border-white/30 hover:bg-cyan-800'>
					<p onClick={() => {
						setSelectedUser('Joaquin Cantero');
						setShowModal(true);
					}} className="cursor-pointer">
						Joaquin Cantero
					</p>
					<button onClick={() => setContextMenuUser('Joaquin Cantero')}>
						<FaEllipsisV />
					</button>

					{/* Mini men√∫ contextual */}
					{contextMenuUser === 'Joaquin Cantero' && (
						<div className="absolute items-center right-2 top-12 w-40 bg-white text-black rounded shadow-lg z-50 p-2">
							<p className="px-4 py-2 hover:bg-gray-200 cursor-pointer">Editar</p>
							<p className="px-4 py-2 hover:bg-gray-200 cursor-pointer">Eliminar</p>
							<p
								onClick={() => setContextMenuUser(null)}
								className="px-4 py-2 text-red-500 hover:bg-red-100 cursor-pointer"
							>
								Cancelar
							</p>
						</div>
					)}
				</article>
				{[
					'Vicente Alvarez',
					'Joaquin Cantero',
					'Vicente Alvarez',
					'Vicente Alvarez',
					'Vicente Alvarez',
					'Vicente Alvarez',
					'Vicente Alvarez'
				].map((name, i) => (
					<article key={i} className='flex flex-row justify-between items-center bg-gray-900 text-white p-4 rounded-xl border border-white/30 hover:bg-cyan-800'>
						<p>{name}</p>
						<button className='justify-items-end'><FaEllipsisV /></button>
					</article>
				))}
			</section>
			{showModal && (
				<div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex items-center justify-center z-50">
					<div className="bg-white rounded-xl p-6 w-96 text-black shadow-lg">
						<h2 className="text-xl font-semibold mb-4">
							{selectedUser ? `Editar: ${selectedUser}` : 'Agregar Usuario'}
						</h2>
						<button
							onClick={() => setShowModal(false)}
							className="mt-4 bg-cyan-700 text-white px-4 py-2 rounded hover:bg-cyan-800"
						>
							Cerrar
						</button>
					</div>
				</div>
			)}
		</div>
	)
}
