import React from 'react';
import { Link, Outlet, useLocation } from 'react-router-dom';
import { FaKey, FaUsers, FaClock, FaPaperPlane, FaCog, FaSignOutAlt } from 'react-icons/fa';

export default function Menu() {
  const location = useLocation();

  const linkClass = (path) =>
    `flex items-center space-x-2 px-4 py-2 rounded-md text-white font-medium transition ${
      location.pathname === path ? 'bg-cyan-700 border border-cyan-300' : 'hover:bg-cyan-800'
    }`;

  return (
    <div className="flex">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white min-h-screen p-4 flex flex-col items-center shadow-md">
        {/* Logo */}
        <div className="mb-6 text-center">
          <div className="text-4xl mb-1">üëÅÔ∏è</div>
          <h1 className="text-2xl font-bold tracking-wide">KEYFORT</h1>
        </div>

        {/* Links */}
        <nav className="flex flex-col w-full space-y-2">
          <Link to="/Passwords" className={linkClass('/Passwords')}>
            <FaKey /> <span>Contrase√±as</span>
          </Link>
          <Link to="/Groups" className={linkClass('/Groups')}>
            <FaUsers /> <span>Grupos</span>
          </Link>
          <Link to="/AccessHistory" className={linkClass('/AccessHistory')}>
            <FaClock /> <span>Historial de acceso</span>
          </Link>
          <Link to="/Send" className={linkClass('/Send')}>
            <FaPaperPlane /> <span>Enviar Contrase√±a</span>
          </Link>
          <Link to="/Settings" className={linkClass('/Settings')}>
            <FaCog /> <span>Ajustes</span>
          </Link>
          <Link to="/logout" className={linkClass('/logout')}>
            <FaSignOutAlt /> <span>Cerrar Sesi√≥n</span>
          </Link>
        </nav>
      </aside>

      {/* Contenido */}
      <main className="flex-1 bg-gray-100 p-6">
        <Outlet />
      </main>
    </div>
  );
}
